import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';

class App extends React.Component {

    constructor() {
        super();

        this.state = {
            jokes: [],
            isFetchingJoke: false
        };

        this.onTellJoke = this.onTellJoke.bind(this);
    }


    componentDidMount() {
        this.searchJokes();
    }

    searchJokes() {
        this.setState({isFetchingJoke: true});
        fetch('https://icanhazdadjoke.com/search', {
            method: 'GET',
            headers: {
                Accept: 'application/json'
            }
        })
            .then(response => response.json())
            .then(json => {
                const jokes = json.results;
                this.setState({
                    jokes,
                    isFetchingJoke: false
                })
            });
    };

    onTellJoke() {
        this.searchJokes();
    };

    render() {

        return (
            <div>
                <form>
                    <input type ="text" placeholder="Enter search keywords..."/>
                    <button> Search </button>
                    <button onClick={() => this.onTellJoke()} disabled={this.state.isFetchingJoke}> Tell me a joke</button>
                </form>
                <p>{this.state.isFetchingJoke? '...': this.state.jokes.toString()}</p>
            </div>
        );
    }
}
const rootElement = document.getElementById('root');
ReactDOM.render(<App version = "1.0"/>, rootElement);
export default App;
