import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';

class App extends React.Component {

    constructor() {
        super();

        this.state = {
            joke: null,
            isFetchingJoke: false
        };

        this.onTellJoke = this.onTellJoke.bind(this);
    }


    componentDidMount() {
        this.fetchJoke();
    }

    fetchJoke() {
        this.setState({isFetchingJoke: true});
        fetch('https://icanhazdadjoke.com/', {
            method: 'GET',
            headers: {
                Accept: 'application/json'
            }
        })
            .then(response => response.json())
            .then(json => {
                this.setState({joke:json.joke, isFetchingJoke: false});
            });
    };

    onTellJoke() {
        this.fetchJoke();
    };

    render() {

        console.log('Render----~~~~~~')

        return (
            <div>
                <form>
                    <input type ="text" placeholder="Enter search keywords..."/>
                    <button> Search </button>
                    <button onClick={() => this.onTellJoke()} disabled={this.state.isFetchingJoke}> Tell me a joke</button>
                </form>
                <p>{this.state.isFetchingJoke? '...': this.state.joke}</p>
            </div>
        );
    }
}
const rootElement = document.getElementById('root');
ReactDOM.render(<App version = "1.0"/>, rootElement);
export default App;
