import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import './SearchForm';
import SearchForm from "./SearchForm";

class App extends React.Component {

    constructor() {
        super();

        this.state = {
            jokes: [],
            isFetchingJokes: false,
            searchTerm: ''
        };

        this.onSearchChange = this.onSearchChange.bind(this);
        this.searchJokes = this.searchJokes.bind(this);
    }


    searchJokes(limit = 20) {
        this.setState({isFetchingJokes: true});
        fetch(`https://icanhazdadjoke.com/search?term=${this.state.searchTerm}&limit=${limit}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json'
            }
        })
            .then(response => response.json())
            .then(json => {
                const jokes = json.results;
                console.log(jokes)
                this.setState({
                    jokes,
                    isFetchingJokes: false
                })
            });
    };

    onSearchChange() {
        this.setState({searchTerm: event.target.value});
    }

    renderJokes() {
        return(
        <ul> {this.state.jokes.map(item => <li key={item.id}>{item.joke}</li>)} </ul>
        );
    }

    render() {

        return (
            <div>
                <SearchForm onFormSubmit={this.searchJokes()}
                onSearchValueChange={this.onSearchChange}
                isSearching={this.state.isFetchingJokes}
                onSingleSearchClick={() => this.searchJokes(1)}/>

                {this.state.isFetchingJokes ? '...' : this.renderJokes()}
            </div>
        );
    }
}
const rootElement = document.getElementById('root');
ReactDOM.render(<App version = "1.0"/>, rootElement);
export default App;
