import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';

class App extends React.Component {

    constructor() {
        super();

        this.state = {
            jokes: [],
            isFetchingJoke: false,
            searchTerm: ''
        };

        this.onTellJoke = this.onTellJoke.bind(this);
        this.onSearchChange = this.onSearchChange.bind(this);
        this.onSearchSubmit = this.onSearchSubmit.bind(this);
    }


    searchJokes(limit = 20) {
        this.setState({isFetchingJoke: true});
        fetch(`https://icanhazdadjoke.com/search?term=${this.state.searchTerm}&limit=${limit}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json'
            }
        })
            .then(response => response.json())
            .then(json => {
                const jokes = json.results;
                console.log(jokes)
                this.setState({
                    jokes,
                    isFetchingJoke: false
                })
            });
    };

    onTellJoke() {
        this.searchJokes();
    };

    onSearchChange(event) {
        this.setState({searchTerm: event.target.value});
    }

    onSearchSubmit(event) {
        event.preventDefault();
        this.searchJokes();
    }

    renderJokes() {
        return(
        <ul> {this.state.jokes.map(item => <li key={item.id}>{item.joke}</li>)} </ul>
        );
    }

    render() {

        return (
            <div>
                <form onSubmit={this.onSearchSubmit}>
                    <input type ="text" placeholder="Enter search keywords..." onChange={this.onSearchChange}/>
                    <button> Search </button>
                    <button onClick={() => this.onTellJoke()} disabled={this.state.isFetchingJoke}> I'm feeling funny </button>
                </form>
                {this.state.isFetchingJoke ? '...' : this.renderJokes()}
            </div>
        );
    }
}
const rootElement = document.getElementById('root');
ReactDOM.render(<App version = "1.0"/>, rootElement);
export default App;
